#!/bin/bash
wget https://repo.zabbix.com/zabbix/6.0/debian/pool/main/z/zabbix-release/zabbix-release_6.0-4+debian11_all.deb && sudo dpkg -i zabbix-release_6.0-4+debian11_all.deb && sudo apt update -y && sudo apt install -y zabbix-server-pgsql zabbix-frontend-php php7.4-pgsql zabbix-apache-conf zabbix-sql-scripts zabbix-agent && sudo su - postgres -c 'psql --command "CREATE USER zabbix WITH PASSWORD '\'123456\'';"' && sudo su - postgres -c 'psql --command "CREATE DATABASE zabbix OWNER zabbix;"' && sudo zcat /usr/share/zabbix-sql-scripts/postgresql/server.sql.gz | sudo -u zabbix psql zabbix && sudo sed -i "s/# DBPassword=/DBPassword=123456/g" /etc/zabbix/zabbix_server.conf && sudo systemctl restart zabbix-server zabbix-agent apache2 && sudo systemctl enable zabbix-server zabbix-agent apache2 && echo "Конец"